function resetActiveBtn(){menuBtns.forEach(function(t){t.classList.remove("active-btn")})}function showFoodMenu(t){activeBtn=t,foodItems.forEach(function(t){t.classList.contains(activeBtn)?t.style.display="grid":t.style.display="none"})}const menuBtns=document.querySelectorAll(".menu-btn"),foodItems=document.querySelectorAll(".food-item");var activeBtn="featured";showFoodMenu(activeBtn),menuBtns.forEach(function(t){t.addEventListener("click",function(){resetActiveBtn(),showFoodMenu(t.id),t.classList.add("active-btn")})});